CREATE VIEW configuration_view AS SELECT backend.name, mode.name, receiver.name, beams.name, polarization.name, bandwidth.name, windows.name, integration.name, switching.name from configuration INNER JOIN backend INNER JOIN mode INNER JOIN receiver INNER JOIN beams INNER JOIN polarization INNER JOIN bandwidth INNER JOIN windows INNER JOIN integration INNER JOIN switching ON configuration.backend_id = backend.id and configuration.mode_id = mode.id and configuration.receiver_id = receiver.id and configuration.beams_id = beams.id and configuration.polarization_id = polarization.id and configuration.bandwidth_id = bandwidth.id and configuration.windows_id = windows.id and configuration.integration_id = integration.id and configuration.switching_id = switching.id;
